import React, {useState} from 'react';
import './post-list-item.css'


const PostListItem = ({label, important}) =>{
    const [starState, setStarState] = useState(important = false);
    const [stateLike, setLikeState] = useState( false);

    const onImportant = () => setStarState(!starState)
    const onLike = () => setLikeState(true)

    let classNameActive = 'app-list-item d-flex justify-content-between';
    if(starState){
        classNameActive += ' important';
    }

    if(stateLike){
        classNameActive += ' like';
    }

    return(
        <div className={classNameActive}>
            <span onClick={onLike} className='app-list-item-label'>
                {label}
            </span>
            <div className='d-flex justify-content-center align-items-center'>
                <button onClick={onImportant} type='button' className='btn-star btn-sm'>
                    <i className='fa fa-star'></i>
                </button>
                <button type='button' className='btn-trash btn-sm'>
                    <i className='fa fa-trash-o'></i>
                </button>
                <i className='fa fa-heart'></i>
            </div>
        </div>
    )
}
export default PostListItem
