import React, {useState} from 'react';
import AppHeader from "../app-header";
import SearchPanel from "../search-panel";
import PostStatusFilter from "../post-status-filter";
import PostList from "../post-list/post-list";
import PostAddForm from "../post-add-form";
import './app.css'


const App = () =>{
    const [stateData, setStateData] = useState({
        data: [
            {label: 'Hello world', important: false, id: 'csdc'},
            {label: 'by products', important: true, id: 'csdcsdcsdc'},
            {label: 'learning js', important: false, id: 'csdcd'},
    ]})

    const onDeleted = (id) =>{
        setStateData(({data})=>{
            const index = data.findIndex((elem) => elem.id === id);
            const newArr = [...data.slice(0,index), ...data.slice(index +1)];
            return{
                data: newArr
            }
        })
    };
    let count = 4
    const onAddItem = (data) =>{
        const newItems = {
            label: data,
            important: false,
            id: count++
        }
        setStateData(({data})=>{
            const newData = [...data, newItems];
            return{
                data: newData
            }
        })
    }

    return(
        <div className='app'>
            <AppHeader />
            <div className='search-panel d-flex'>
                <SearchPanel />
                <PostStatusFilter />
            </div>
            <PostList onDeleted={onDeleted} posts={stateData} />
            <PostAddForm addNewElements={onAddItem} />
        </div>

    )
};

export default App
