import React, {useState} from 'react';
import AppHeader from "../app-header";
import SearchPanel from "../search-panel";
import PostStatusFilter from "../post-status-filter";
import PostList from "../post-list/post-list";
import PostAddForm from "../post-add-form";
import './app.css'


const App = () =>{
    const [stateData, setStateData] = useState({
        data: [
            {label: 'Hello world', important: false, like: false, id: 'csdc'},
            {label: 'by products', important: true, like: false, id: 'csdcsdcsdc'},
            {label: 'learning js', important: false, like: false, id: 'csdcd'},
    ]})

    const onDeleted = (id) =>{
        setStateData(({data})=>{
            const index = data.findIndex((elem) => elem.id === id);
            const newArr = [...data.slice(0,index), ...data.slice(index +1)];
            return{
                data: newArr
            }
        })
    };

    const onAddItem = (data) =>{
        const newItems = {
            label: data,
            important: false,
        }
        setStateData(({data})=>{
            const newData = [...data, newItems];
            return{
                data: newData
            }
        })
    }
    const onToggleImportant = (id) =>{
        setStateData(({data}) =>{
            const index = data.findIndex(elem => elem.id === id);
            const old = data[index];
            const newItem = {...old, important: !old.important};
            const newArr = [...data.slice(0,index), newItem, ...data.slice(index +1)];
            return{
                "data": newArr
            }
        })
    }


    const onToggleLiked = (id) =>{
        setStateData(({data}) =>{
            const index = data.findIndex(elem => elem.id === id);
            const old = data[index];
            const newItem = {...old, like: !old.like};
            const newArr = [...data.slice(0,index), newItem, ...data.slice(index +1)];
            return{
                data: newArr
            }
        })
    }
    const {data} = stateData;
    const liked = data.filter(item => item.like).length;
    const allPosts = data.length

    return(
        <div className='app'>
            <AppHeader liked={liked} allPosts={allPosts}/>
            <div className='search-panel d-flex'>
                <SearchPanel />
                <PostStatusFilter />
            </div>
            <PostList onDeleted={onDeleted}
                      posts={stateData}
                      onToggleImportant={onToggleImportant}
                      onToggleLiked={onToggleLiked} />
            <PostAddForm addNewElements={onAddItem} />
        </div>

    )
};

export default App
