import React, {useState} from 'react';
import AppHeader from "../app-header";
import SearchPanel from "../search-panel";
import PostStatusFilter from "../post-status-filter";
import PostList from "../post-list/post-list";
import PostAddForm from "../post-add-form";
import './app.css'


const App = () =>{
    const [stateData, setStateData] = useState([
        {label: 'Hello world', important: false, id: 'csd'},
        {label: 'by products', important: true, id: 'csdc'},
        {label: 'learning js', important: false, id: 'csdcsd'},
    ])

    const onDeleted = (id) =>{
        setStateData(({stateData})=>{
            const index = stateData.indexOf((elem) => elem.id === id);
            const newArr = [...stateData.slice(0,index), ...stateData.slice(index,+1)];
            return{
                data: newArr
            }
        })
    }
    return(
        <div className='app'>
            <AppHeader />
            <div className='search-panel d-flex'>
                <SearchPanel />
                <PostStatusFilter />
            </div>
            <PostList onDeleted={onDeleted} posts={stateData} />
            <PostAddForm />
        </div>

    )
};

export default App
